supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false

 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder

 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }

 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$global:LASTEXITCODE = $null
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -and $LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

ns9:component-version-script.script:
@ECHO OFF
IF [%automation_jar%] EQU [] (
echo no updates found
) ELSE (
del c:\ADFSAutomation\lib\ADFSAutomation.jar
copy %automation_jar% c:\ADFSAutomation\lib
ECHO ADFS Automation jar updated
)

# Licensed to the iGate under one #--------------------------------------------------------------------------- # Startup script for the prepare stand alone ADFS # Environment Variable Prequisites # JAVA_HOME : Must point to your JDK/JRE. # ADFSAutomation_HOME : should point to path where automation files are located #--------------------------------------------------------------------------- # Automation home folder Set-Location $env:ADFSAutomation_HOME
# Make sure prerequisite environment variables are set if ("$env:java_HOME" -eq "" -and "$env:ADFSAutomation_HOME" -eq "") { write-output "The JAVA_HOME / ADFSAutomation_HOME environment variable is not defined" write-output "These environment variables are needed to run this program" exit -1 } else { $ChkFile = "$env:JAVA_HOME\bin\java.exe" $FileExists = (Test-Path $ChkFile -PathType Leaf) if (!$FileExists) { write-output "The JAVA_HOME environment variable is not defined correctly" write-output "This environment variable is needed to run this program" write-output "NB: JAVA_HOME should point to a JDK/JRE" exit -1 } else { # Loop through the libs and add them to the class path $env:ADFSAutomation_CLASS_PATH = "$env:ADFSAutomation_HOME\lib" Foreach ($file in Get-Childitem "$env:ADFSAutomation_CLASS_PATH") { If ($file.extension -eq ".jar") { $ADFSAutomation_CLASS_PATH = "$ADFSAutomation_CLASS_PATH;" + "$env:ADFSAutomation_CLASS_PATH\$file" } }
# Get the IP address of virtual machine $IP = ((ipconfig | findstr [0-9].\.)[0]).Split()[-1] switch (($IP -split '\.')[2]) { 254 {$location = 1} 239 {$location = 2} default {$location = 0} }
# Assign the parameters & their values to variables & pass them to jave exe $vcoServerIP = "vcoServerIP=$VCO_SERVER_IP" $vcoServerPort = "vcoServerPort=$VCO_SERVER_PORT" $vcoAdminGrpUsername = "vcoAdminGrpUsername=$VCO_ADMIN_USER" $vcoAdminGrpPasswd = "vcoAdminGrpPasswd=$VCO_ADMIN_PASSWORD" $workFlowName = "workflowName=$VCO_WORKFLOW_NAME"
$claimproviderIPAddr = "claimproviderIPAddr=$CLAIM_PROVIDER_IP_ADDRESS" $claimproviderHostName = "claimproviderHostName=$CLAIM_PROVIDER_HOST_NAME" $claimproviderCertpath = "claimproviderCertpath=$CLAIM_PROVIDER_CERTIFICATE" $claimProRuleName = "claimProRuleName=$CLAIM_PROVIDER_RULE"
$resourceADFSvmIP = "resourceADFSvmIP=$IP" $resourceADFSvmUsr = "resourceADFSvmUsr=$ADFS_VM_ADMIN_USER" $resourceADFSvmPassd = "resourceADFSvmPassd=$ADFS_VM_ADMIN_PASSWORD" $resCertThumbprint = "resCertThumbprint=$RES_CERT_THUMBPRINT" $debugFlag = "DebugSensitive=true"
write-output "Virtual Machine IP address : $IP" write-output "Using JAVA_HOME : $env:JAVA_HOME" write-output "Using ADFSAutomation_HOME : $env:ADFSAutomation_HOME" write-output "Using ADFSAutomation_CLASS_PATH : $ADFSAutomation_CLASS_PATH"
$env:_RUNJAVA="$env:JAVA_HOME\bin\java" & "$env:_RUNJAVA" "$env:JAVA_OPTS" ‘-cp’ "$ADFSAutomation_CLASS_PATH" com.igate.adfsauto.client.AdfsAutoWSClient $vcoServerIP $vcoServerPort $vcoAdminGrpUsername $vcoAdminGrpPasswd $workFlowName $resourceADFSvmIP $resourceADFSvmUsr $resourceADFSvmPassd $resCertThumbprint $claimproviderIPAddr $claimproviderHostName $claimproviderCertpath $claimProRuleName $debugFlag
$retVal = $? if ($retVal) { write-output "ADFS Configuration successful" exit 0 } else { write-output "ADFS Configuration failed" exit -1 } } }

