# Licensed to the iGate under one #--------------------------------------------------------------------------- # Startup script for the prepare stand alone ADFS # Environment Variable Prequisites # JAVA_HOME : Must point to your JDK/JRE. # ADFSAutomation_HOME : should point to path where automation files are located #--------------------------------------------------------------------------- # Automation home folder Set-Location $env:ADFSAutomation_HOME
# Make sure prerequisite environment variables are set if ("$env:java_HOME" -eq "" -and "$env:ADFSAutomation_HOME" -eq "") { write-output "The JAVA_HOME / ADFSAutomation_HOME environment variable is not defined" write-output "These environment variables are needed to run this program" exit -1 } else { $ChkFile = "$env:JAVA_HOME\bin\java.exe" $FileExists = (Test-Path $ChkFile -PathType Leaf) if (!$FileExists) { write-output "The JAVA_HOME environment variable is not defined correctly" write-output "This environment variable is needed to run this program" write-output "NB: JAVA_HOME should point to a JDK/JRE" exit -1 } else { # Loop through the libs and add them to the class path $env:ADFSAutomation_CLASS_PATH = "$env:ADFSAutomation_HOME\lib" Foreach ($file in Get-Childitem "$env:ADFSAutomation_CLASS_PATH") { If ($file.extension -eq ".jar") { $ADFSAutomation_CLASS_PATH = "$ADFSAutomation_CLASS_PATH;" + "$env:ADFSAutomation_CLASS_PATH\$file" } }
# Get the IP address of virtual machine $IP = ((ipconfig | findstr [0-9].\.)[0]).Split()[-1] switch (($IP -split '\.')[2]) { 254 {$location = 1} 239 {$location = 2} default {$location = 0} }
# Assign the parameters & their values to variables & pass them to jave exe $vcoServerIP = "vcoServerIP=$VCO_SERVER_IP" $vcoServerPort = "vcoServerPort=$VCO_SERVER_PORT" $vcoAdminGrpUsername = "vcoAdminGrpUsername=$VCO_ADMIN_USER" $vcoAdminGrpPasswd = "vcoAdminGrpPasswd=$VCO_ADMIN_PASSWORD" $workFlowName = "workflowName=$VCO_WORKFLOW_NAME"
$claimproviderIPAddr = "claimproviderIPAddr=$CLAIM_PROVIDER_IP_ADDRESS" $claimproviderHostName = "claimproviderHostName=$CLAIM_PROVIDER_HOST_NAME" $claimproviderCertpath = "claimproviderCertpath=$CLAIM_PROVIDER_CERTIFICATE" $claimProRuleName = "claimProRuleName=$CLAIM_PROVIDER_RULE"
$resourceADFSvmIP = "resourceADFSvmIP=$IP" $resourceADFSvmUsr = "resourceADFSvmUsr=$ADFS_VM_ADMIN_USER" $resourceADFSvmPassd = "resourceADFSvmPassd=ca`$hc0w" ## $resourceADFSvmPassd = "resourceADFSvmPassd=$ADFS_VM_ADMIN_PASSWORD" $resCertThumbprint = "resCertThumbprint=$RES_CERT_THUMBPRINT"
write-output "Virtual Machine IP address : $IP" write-output "Using JAVA_HOME : $env:JAVA_HOME" write-output "Using ADFSAutomation_HOME : $env:ADFSAutomation_HOME" write-output "Using ADFSAutomation_CLASS_PATH : $ADFSAutomation_CLASS_PATH"
$env:_RUNJAVA="$env:JAVA_HOME\bin\java" & "$env:_RUNJAVA" "$env:JAVA_OPTS" ‘-cp’ "$ADFSAutomation_CLASS_PATH" com.igate.adfsauto.client.AdfsAutoWSClient $vcoServerIP $vcoServerPort $vcoAdminGrpUsername $vcoAdminGrpPasswd $workFlowName $resourceADFSvmIP $resourceADFSvmUsr $resourceADFSvmPassd $resCertThumbprint $claimproviderIPAddr $claimproviderHostName $claimproviderCertpath $claimProRuleName
$retVal = $? if ($retVal) { write-output "ADFS Configuration successful" exit 0 } else { write-output "ADFS Configuration failed" exit -1 } } }

